a {
  color: white;
  text-decoration: none;
}

.site-wrapper {
  display: grid;
  grid-template-rows: 5em [header];
  grid-template-columns: 70vw [main] 30vw [side];
  height: 100vh;
  background: #3650a1;
}

.header {
  grid-row: 1/2;
  grid-column: 1/3;
  display: flex;
  justify-content: flex-start;
  align-items: center;
  color: white;
  font-size: 2rem;
  padding-left: 2rem;
}
@media only screen and (max-width: 768px) {
  .header {
    justify-content: center;
    padding: 0;
  }
}
@media only screen and (min-width: 1200px) {
  .header {
    font-size: 4rem;
  }
}

.home {
  grid-row: 2/3;
  grid-column: 1/3;
  display: flex;
  justify-content: center;
  align-items: flex-start;
  text-align: center;
}
.home .content {
  width: 80vw;
}
.home h1 {
  display: inline-block;
  color: white;
  background: black;
  padding: 5px;
  font-size: 1.5rem;
  text-align: center;
}
.home .button-group {
  display: flex;
  justify-content: space-between;
  font-size: 4rem;
}
@media only screen and (max-width: 768px) {
  .home .button-group {
    flex-direction: column;
    align-items: center;
    font-size: 2em;
  }
}
.home .button-group div {
  background: black;
  height: 20vw;
  width: 20vw;
  display: flex;
  justify-content: center;
  align-items: center;
}
@media only screen and (max-width: 768px) {
  .home .button-group div {
    margin: 2rem;
    width: 80vw;
    height: 20vw;
  }
}
.home .button-group div a {
  max-width: 80%;
  color: white;
  text-decoration: none;
  text-align: center;
}
